import { DatabaseTree } from '../types/tree.js';

/**
 * Converts database into types similar to kanel.js
 *
 * There are a couple different types of files.
 *
 * Each schema has a subdirectory.
 *
 **/

interface TreeToTypescriptOptions {
    primarySchemaName?: string;
    injectedTypesDirectory?: string;
}
declare const treeToTypescriptModels: (db: DatabaseTree, { primarySchemaName, injectedTypesDirectory, }?: TreeToTypescriptOptions) => {
    [filePath: string]: string;
};

export { treeToTypescriptModels as default, treeToTypescriptModels };
